import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Ensure this is imported
import Navbar from './Navbar';
import Footer from './Footer';
import './register.css';

const Register = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Basic validation for phone number
        if (!phoneNumber || phoneNumber.trim() === '' || !isValidPhoneNumber(phoneNumber)) {
            alert('Please enter a valid phone number');
            return;
        }

        const response = await fetch('http://localhost:5000/api/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ username, password, phoneNumber })
        });
        if (response.ok) {
            alert('Registration successful!');
            navigate('/login');
        } else {
            alert('Registration failed!');
        }
    };

    const isValidPhoneNumber = (phoneNumber) => {
        // You can add your validation logic here
        // For example, check if it contains only digits and is of a valid length
        return /^\d{10}$/.test(phoneNumber); // Assuming a 10-digit phone number
    };

    return (
        <div>
            <Navbar /><br /><br /><br /><br /><br />
            
            <div className="register-form">
                <h2>Register</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                   <input
                        type="tel"
                        placeholder="Phone Number"
                        value={phone_no}
                        onChange={(e) => setPhoneNumber(e.target.value)}
                        required
                        minLength="10"
                    />

                    <button type="submit">Register</button>
                </form>
            </div><br /><br /><br /><br /><br /><br /><br /><br />
            <Footer />
        </div>
    );
};

export default Register;
