// CaseManagement.js
import React from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory for navigation

const CaseManagement = () => {
    const history = useHistory(); // Use useHistory hook inside the functional component

    // Function to handle button click
    const handleClick = (action) => {
        switch (action) {
            case 'view cases':
                alert('Navigating to view cases page');
                history.push('/view-cases'); // Use history.push for navigation
                break;
            case 'manage tasks':
                alert('Opening task management');
                // Perform other actions for manage tasks
                break;
            case 'view reports':
                alert('Showing detailed reports');
                // Perform other actions for view reports
                break;
            default:
                break;
        }
    };

    return (
        <div className="service-section">
            <h2>Case Management</h2>
            <div className="feature">
                <h3>Organize Cases</h3>
                <p>Efficiently organize and manage case files.</p>
                <button className="button" onClick={() => handleClick('view cases')}>View Cases</button>
            </div>
            <div className="feature">
                <h3>Task Management</h3>
                <p>Assign tasks and track progress for each case.</p>
                <button className="button" onClick={() => handleClick('manage tasks')}>Manage Tasks</button>
            </div>
            <div className="feature">
                <h3>Reports</h3>
                <p>Generate reports and insights for better decision-making.</p>
                <button className="button" onClick={() => handleClick('view reports')}>View Reports</button>
            </div>
        </div>
    );
};

export default CaseManagement;
