import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './CalendarView.css';
import Navbar from './Navbar';
import Footer from './Footer';

const CalendarView = () => {
    const [appointments, setAppointments] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date());

    useEffect(() => {
        fetchAppointments(selectedDate);
    }, [selectedDate]);

    const fetchAppointments = async (date) => {
        try {
            const response = await fetch(`http://localhost:5000/api/appointments?date=${date.toISOString().split('T')[0]}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setAppointments(data);
        } catch (error) {
            console.error('Error fetching appointments:', error);
        }
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    return (
        <div>
            <Navbar />
            <div>
                <h2>Appointment Calendar</h2>
                <Calendar
                    onChange={handleDateChange}
                    value={selectedDate}
                />
                <div className="appointment-details">
                    <h3>Appointments on {selectedDate.toDateString()}</h3>
                    <ul>
                        {appointments.map((appointment, index) => (
                            <li key={index}>
                                {appointment.client_name} at {new Date(appointment.appointment_date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default CalendarView;
