import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './CalendarView.css';
import Navbar from './Navbar';
import Footer from './Footer';

const CalendarView = () => {
    const [appointments, setAppointments] = useState([]);
    const [date, setDate] = useState(new Date());

    useEffect(() => {
        fetchAppointments();
    }, []);

    const fetchAppointments = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/appointments');
            const data = await response.json();
            setAppointments(data);
        } catch (error) {
            console.error('Error fetching appointments:', error);
        }
    };

    const tileClassName = ({ date, view }) => {
        if (view === 'month') {
            const dateString = date.toISOString().split('T')[0];
            const appointmentDates = appointments.map(appointment => appointment.appointment_date.split('T')[0]);
            if (appointmentDates.includes(dateString)) {
                return 'booked';
            }
        }
    };

    return (
        <div>
            <Navbar/>
            <div>
                <h2>Appointment Calendar</h2>
                <Calendar
                    onChange={setDate}
                    value={date}
                    tileClassName={tileClassName}
                />
                <div className="appointment-details">
                    <h3>Appointments on {date.toDateString()}</h3>
                    <ul>
                        {appointments
                            .filter(appointment => appointment.appointment_date.split('T')[0] === date.toISOString().split('T')[0])
                            .map((appointment, index) => (
                                <li key={index}>
                                    {appointment.client_name} at {new Date(appointment.appointment_date).toLocaleTimeString()}
                                </li>
                            ))
                        }
                    </ul>
                </div>
            </div>
            <Footer/>
        </div>
    );
};

export default CalendarView;
