// ReceiveMessage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CryptoJS from 'crypto-js';

const ReceiveMessage = () => {
  const [receivedMessage, setReceivedMessage] = useState('');

  useEffect(() => {
    const fetchMessage = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/receive-message');
        const decryptedMessage = CryptoJS.AES.decrypt(response.data.encryptedMessage, 'secretKey').toString(CryptoJS.enc.Utf8);
        setReceivedMessage(decryptedMessage);
      } catch (error) {
        console.error('Error fetching message:', error);
      }
    };

    fetchMessage();
  }, []);

  return (
    <div>
      {receivedMessage ? (
        <div>
          <h3>Received Message</h3>
          <p>{receivedMessage}</p>
        </div>
      ) : (
        <p>No message received</p>
      )}
    </div>
  );
};

export default ReceiveMessage;
